mapper.select:
SELECT COUNT(*) AS TOTAL FROM lin_user u WHERE u.id IN ( SELECT sc.user_id FROM student_class AS sc WHERE sc.class_id = ( SELECT w.class_id from work AS w WHERE w.id = #{workId} AND w.delete_time IS NULL )
AND (u.username LIKE CONCAT('%', #{username}, '%') OR u.nickname LIKE CONCAT('%', #{username}, '%'))
)
